hooks.hook:
// tag handling function
function handle_mp3_tag($inputText) {
    return '<audio src="'.$inputText.'" controls preload></audio>';
}

// add our tag to the list
$tags[] = 'mp3';
$tags_opened[] = 'mp3';
$tags_closed[] = 'mp3';
$tags_inline[] = 'mp3';
$tags_trim[] = 'mp3';
$tags_limit_bbcode['*'][] = 'mp3';
$tags_limit_bbcode['url'][] = 'mp3';
$tags_limit_bbcode['email'][] = 'mp3';
